export default {

  getBooks: function (search) {
    try {
      const query = JSON.stringify(search)
      const response = fetch(`https://www.googleapis.com/books/v1/volumes?q=intitle:${query}&key=AIzaSyDIgzatOnslxJ_o79ySgfXYrKrbOGahZK0`);
      if (response.ok) {
        const jsonResponse = response.json();
        return jsonResponse;
      }
      throw new Error("Request failed");
    } catch(error) {
      console.log(error);
    }
  },

  saveBook: ({ bookData }) => {
    try {
      const response = fetch('http://localhost:3000/api/book', {
        method: "POST",
        body: JSON.stringify({bookData})
      });
      if (response.ok) {
        const jsonResponse = response.json();
        return jsonResponse;
      }
      throw new Error("Request failed");
    } catch(error) {
      console.log(error);
    }
  }

}
