export default {

  getBooks: async (query) => {
    try {
      const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=intitle:${query}&key=AIzaSyDIgzatOnslxJ_o79ySgfXYrKrbOGahZK0`);
      if (response.ok) {
        const jsonResponse = await response.json();
        return jsonResponse;
      }
      throw new Error("Request failed");
    } catch(error) {
      console.log(error);
    }
  },

  saveBook: async ({ bookData }) => {
    try {
      const response = await fetch('http://localhost:3000/api/book', {
        method: "POST",
        body: JSON.stringify({bookData})
      });
      if (response.ok) {
        const jsonResponse = await response.json();
        return jsonResponse;
      }
      throw new Error("Request failed");
    } catch(error) {
      console.log(error);
    }
  }

}
